import React, { useEffect, useState, useRef } from 'react';
import CardData from './CardData'
import Card from './Card'



export default function App() {
  const x = new CardData();


  const [questions, setQuestions] = useState([]);

  const cards = x.arrayOfMaps.map(item => {
    return (
      <Card 
          key={item.id}
          question={item.question}
          answer={item.answer}
      />
    )
  })

  const questionRef = useRef();
  const answerRef = useRef();

  function handleClick(e) {
    // console.log("Question: " + questionRef.current.value)
    // console.log("Answer: " + answerRef.current.value)
    // questions.push(questionRef.current.value);
    x.pushToArray(questionRef.current.value, answerRef.current.value)
    //x.clearArray();
    setQuestions(x.arrayOfMaps);
    // console.log(x.arrayOfMaps);
  }

  useEffect(() => {
    console.log(questions); // Log the questions array whenever it changes
  }, [questions]);

  return (
    <div>
      <input ref={questionRef} type="text" placeholder="Question"></input>
      <input ref={answerRef} type="text" placeholder="Answer"></input>
      <button onClick={handleClick}>Submit</button>
      {cards}
    </div>
  )
}
