import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './style.css';
import CardList from './CardList';
import Card from './Card';

export default function Select() {
  const sets = JSON.parse(localStorage.getItem('flashcardSets')) || [];

  const [selectedOption, setSelectedOption] = useState('');
  const [showResult, setShowResult] = useState(false); // State variable to control rendering
  const [currentCardIndex, setCurrentCardIndex] = useState(0);
  const [showAllCards, setShowAllCards] = useState(false); // State variable to control card view mode

  const handleChange = (event) => {
    setSelectedOption(event.target.value);
    setShowResult(false); // Reset the showResult state when the option changes
    setCurrentCardIndex(0); // Reset the current card index when the option changes
    setShowAllCards(false); // Reset the card view mode when the option changes
  };

  const handleViewSet = () => {
    const selectedSet = sets.find((set) => set.name === selectedOption);

    if (selectedSet) {
      const cards = selectedSet.cards;
      const currentCard = cards[currentCardIndex];

      const handleNext = () => {
        setCurrentCardIndex((prevIndex) =>
          prevIndex === cards.length - 1 ? 0 : prevIndex + 1
        );
      };

      const handlePrev = () => {
        setCurrentCardIndex((prevIndex) =>
          prevIndex === 0 ? cards.length - 1 : prevIndex - 1
        );
      };

      return (
        <div>
          <Card question={currentCard.question} answer={currentCard.answer} />
          <button onClick={handlePrev}>Previous</button>
          <button onClick={handleNext}>Next</button>
          <p>{`Card ${currentCardIndex + 1} / ${cards.length}`}</p>
        </div>
      );
    } else {
      return null;
    }
  };

  const handleToggleView = () => {
    setShowAllCards((prevState) => !prevState); // Toggle the showAllCards state
  };

  const handleClick = () => {
    setShowResult(true); // Set showResult to true when the button is clicked
  };

  return (
    <div>
      <select value={selectedOption} onChange={handleChange}>
        <option value="">Select an option</option>
        {sets.map((set) => (
          <option key={set.id} value={set.name}>
            {set.name}
          </option>
        ))}
      </select>

      <button className="btn btn-light" onClick={handleClick}>Show Result</button>
      <button className="btn btn-light" onClick={handleToggleView}>
        {showAllCards ? 'Switch to Single Card View' : 'Switch to Grid View'}
      </button>

      {showResult && showAllCards && (
        <CardList questions={sets[currentCardIndex].cards} />
      )}

      {showResult && !showAllCards && handleViewSet()}
    </div>
  );
}
