import React, { useState, useEffect } from 'react';
import Card from './Card';
import './style.css';

export default function CardSetCard(props) {
  const [cards, setCards] = useState([]);

  function handleClick() {
    const storedCards = JSON.parse(localStorage.getItem(props.name));
    if (storedCards) {
      setCards(storedCards);
    }
  }

  useEffect(() => {
    const storedCards = JSON.parse(localStorage.getItem(props.name));
    if (storedCards) {
      setCards(storedCards);
    }
  }, [props.name]);

  return (
    <div className={`setCard `} onClick={() => handleClick()}>
      <div className="front">{props.name}</div>
      {cards.length > 0 && (
        <div className="cardsContainer">
          {cards.map((card) => (
            <Card key={card.id} question={card.question} answer={card.answer} />
          ))}
        </div>
      )}
    </div>
  );
}
